export default function question(props){

    function handleChange(event){
        props.handleChange(event, props.index)
    }

    let name

    let ans = ["ans"+index+"1", "ans"+index+"2", "ans"+index+"3", "ans"+index+"4"]

    console.log("name")
    console.log(name)


    return(
        <div>
            <h3>{props.element.question}</h3>
            <form className="boxed">
                <ul>
                    <li className={name}>
                        <input
                            className={name}
                            type="radio"
                            id={ans[0]}
                            name="answer"
                            value={props.element.answers[0]}
                            onhange={handleChange}
                            checked={props.element.answer[props.index] === props.element.answers[0]} 
                        />
                        <label for={ans[0]}>{props.element.answers[0]}</label>
                    </li>
                    <li className={name}>
                        <input
                            className={name}
                            type="radio"
                            id={ans[1]}
                            name="answer"
                            value={props.element.answers[1]}
                            onChange={handleChange}
                            // checked={props.element.answer[props.index] === props.element.answers[1]}    
                        />
                        <label for={ans[1]}>{props.element.answers[1]}</label>
                    </li>
                    <li className={name}>
                        <input
                            className={name}
                            type="radio"
                            id={ans[2]}
                            name="answer"
                            value={props.element.answers[2]}
                            onChange={handleChange}
                            // checked={props.element.answer[props.index] === props.element.answers[2]}   
                        />
                        <label for={ans[2]}>{props.element.answers[2]}</label>
                    </li>
                    <li className={name}>
                        <input
                            className={name}
                            type="radio"
                            id={ans[3]}
                            name="answer"
                            value={props.element.answers[3]}
                            onChange={handleChange}
                            // checked={props.element.answer[props.index] === props.element.answers[3]}   
                        />
                        <label for={ans[3]}>{props.element.answers[3]}</label>              
                    </li>
                </ul>
            </form>
            <hr/>
        </div>
    )
}
