export default function question(props){

    function handleChange(event){

        props.handleChange(event, index)
        // const {name, value} = event.target
        // setFormData(prevFormData => {
        //     return{
        //         ...prevFormData,
        //         [name] : value
        //     }
        // })
    }

    return(
        <div>
            <h3>{props.element.question}</h3>
            {/* { props.answers[0] && <button className="question-btn">{props.answers[0]}</button>}
            { props.answers[1] && <button className="question-btn">{props.answers[1]}</button>}
            { props.answers[2] && <button className="question-btn">{props.answers[2]}</button>}
            { props.answers[3] && <button className="question-btn">{props.answers[3]}</button>} */}

            <form className="boxed">
                <fieldset>
                    <input
                        type="radio"
                        id="ans1"
                        name="answer"
                        value={props.element.answers[0]}
                        onChange={handleChange}
                        checked=""    
                    />
                    <label for="ans1">{props.element.answers[0]}</label>
                    <input
                        type="radio"
                        id="ans2"
                        name="answer"
                        value={props.element.answers[1]}
                        onChange={handleChange}
                        checked=""    
                    />
                    <label for="ans2">{props.element.answers[1]}</label>
                    <input
                        type="radio"
                        id="ans3"
                        name="answer"
                        value={props.element.answers[2]}
                        onChange={handleChange}
                        checked=""    
                    />
                    <label for="ans3">{props.element.answers[2]}</label>
                    <input
                        type="radio"
                        id="ans4"
                        name="answer"
                        value={props.element.answers[3]}
                        onChange={handleChange}
                        checked=""    
                    />
                    <label for="ans4">{props.element.answers[4]}</label>              
                </fieldset>
            </form>
            <hr/>
        </div>
    )
}
