export default function question(props){

    function handleChange(event){
        props.handleChange(event, props.index)
    }

    console.log("HERE")
    console.log(props.element.answers[0])

    return(
        <div>
            <h3>{props.element.question}</h3>
            <form className="boxed">
                <fieldset>
                    <input
                        type="radio"
                        id="ans1"
                        name="answer"
                        value={props.element.answers[0]}
                        onChange={handleChange}
                        checked={props.element.answer[props.index] === props.element.answers[0]} 
                    />
                    <label for="ans1">{props.element.answers[0]}</label>
                    <input
                        type="radio"
                        id="ans2"
                        name="answer"
                        value={props.element.answers[1]}
                        onChange={handleChange}
                        checked={props.element.answer[props.index] === props.element.answers[1]}    
                    />
                    <label for="ans2">{props.element.answers[1]}</label>
                    <input
                        type="radio"
                        id="ans3"
                        name="answer"
                        value={props.element.answers[2]}
                        onChange={handleChange}
                        checked={props.element.answer[props.index] === props.element.answers[2]}   
                    />
                    <label for="ans3">{props.element.answers[2]}</label>
                    <input
                        type="radio"
                        id="ans4"
                        name="answer"
                        value={props.element.answers[3]}
                        onChange={handleChange}
                        checked={props.element.answer[props.index] === props.element.answers[3]}   
                    />
                    <label for="ans4">{props.element.answers[3]}</label>              
                </fieldset>
            </form>
            <hr/>
        </div>
    )
}
