export default function question(props){

    return(
        <div>
            <h3>{props.question}</h3>
            { props.answers[0] && <button className="question-btn">{props.answers[0]}</button>}
            { props.answers[1] && <button className="question-btn">{props.answers[1]}</button>}
            { props.answers[2] && <button className="question-btn">{props.answers[2]}</button>}
            { props.answers[3] && <button className="question-btn">{props.answers[3]}</button>}
            <hr/>
        </div>
    )
}
