import React from "react"
import Question from "./Question"

export default function QuestionsPage(props){
    const [questions, setQuestions] = React.useState([])
    const [questionsDB, setQuestionsDB] = React.useState([])

    // const questionsDB = {}

    function updateQuestions (question) {
        setQuestions(prevQuestions => {
            if (!prevQuestions)
                prevQuestions = question
            else
                prevQuestions.push(question)
            return {
                prevQuestions
            }
        })
    }

    React.useEffect(() => {
        fetch("https://opentdb.com/api.php?amount=5&type=multiple")
            .then(res => res.json())
            .then(data => setQuestionsDB(data.results))
    }, [props.firstPage])

    // console.log("moi")
    // console.log(questionsDB)
    const questionElements = questionsDB.map((question, index) =>{
        let answers = question.incorrect_answers
        // const randomNumber = Math.floor(Math.random() * 3)
        // console.log("Answers")
        // console.log(answers)
        answers = answers.filter(function(item, pos) {
            return answers.indexOf(item) == pos;
        })
        answers = answers.filter(function( element ) {
            return element !== undefined;
         });

        (!answers.includes(question.correct_answer) && answers.splice(Math.floor(Math.random() * 3), 0, question.correct_answer))
        // answers.splice(randomNumber, 0, question.correct_answer);
        // console.log(answers)


        const finalAnswers = answers.map(answer => {

            return(
                answer.replace(/&lt;/g, "<")
                    .replace(/&Gt;/g, ">")
                    .replace(/&#039;/g, "'")
                    .replace(/&amp;/g, "")
                    .replace(/&ouml;/g, "o")
                    .replace(/&quot;/g, "")
                    .replace(/&ldquo;/g, `"`)
                    .replace(/&eacute;/g, `e`)


            )
        } )
        let finalQuestion = question.question
        console.log("Question")
        console.log(finalQuestion)
        finalQuestion.replace(/&lt;/g, "<")
            .replace(/&Gt;/g, ">")
            .replace(/&#039;/g, "")
            .replace(/&amp;/g, "")
            .replace(/&ouml;/g, "o")
            .replace(/&quot;/g, "")
            .replace(/&ldquo;/g, `"`)
            .replace(/&eacute;/g, `e`)

        console.log(finalQuestion)
        element = {
            correct_answer : question.correct_answer,
            answers : finalAnswers,
            question : finalQuestion,
            answer : "",
            key : index
        }
        updateQuestions(element)
        return(
            <Question 
                key={index}
                element={questions[index]}
                handleChange={handleChange}
                // question={finalQuestion}
                // answers={finalAnswers}

            />
        )
    })


    function handleChange(event){
        const {name, value} = event.target
        setFormData(prevFormData => {
            return{
                ...prevFormData,
                [name] : value
            }
        })
    }


    return(
        <div className="questions-page">
            {questionElements}
            <button>Check Answers</button>
        </div>
    )
}
